import{_ as o,o as t,c as a,a3 as n}from"./chunks/framework.Dam_eVYr.js";const u=JSON.parse('{"title":"Konado Installation Guide","description":"","frontmatter":{},"headers":[],"relativePath":"en/tutorial/install.md","filePath":"en/tutorial/install.md","lastUpdated":1760274646000}'),i={name:"en/tutorial/install.md"};function d(l,e,r,s,c,h){return t(),a("div",null,[...e[0]||(e[0]=[n('<h1 id="konado-installation-guide" tabindex="-1">Konado Installation Guide <a class="header-anchor" href="#konado-installation-guide" aria-label="Permalink to &quot;Konado Installation Guide&quot;">​</a></h1><h2 id="dependencies-and-environment" tabindex="-1">Dependencies and Environment <a class="header-anchor" href="#dependencies-and-environment" aria-label="Permalink to &quot;Dependencies and Environment&quot;">​</a></h2><p>Konado requires Godot 4.4 or later. It is recommended to use the latest version of Godot.</p><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><ol><li>Create <code>addons</code> folder in your Godot project folder, if it does not exist.</li><li>Download the latest release zip file from the release page.</li><li>Extract the zip file to the <code>addons</code> folder (This will create a <code>konado</code> folder inside <code>addons</code>).</li><li>Enable the Konado addon in your project settings, to do this, go to <code>Project -&gt; Project Settings -&gt; Addons</code> and check the konado addon. Then save and reopen the project.</li></ol><p><strong>Tip:</strong> When you install Konado for the first time, Godot may print some error messages. You can safely ignore these, as they will be resolved after you enable the addon and reopen the project.</p>',6)])])}const f=o(i,[["render",d]]);export{u as __pageData,f as default};
